import exgcd
import random
import modEx

def getpq(n,e,d):
    k=e*d-1
    t=1
    g=random.randint(2,n-1)
    while(True):
        while t%2!=0:
            g=random.randint(2,n-1)
            t=k
        t=t//2
        x=modEx.quick_modEx(g,t,n)
        if x>1 and exgcd.gcd(x-1,n)>1:
            p=exgcd.gcd(x-1,n)
            q=n//exgcd.gcd(x-1,n)
            print("p=%d\nq=%d"%(p,q))
            print(p*q==n)
            return 
    
